import React from "react";

export default function CountryList({ countries, onDelete, onSelect }) {
  return (
    <ul>
      {countries.map((c) => (
        <li key={c._id}>
          {c.name} ({c.code})
          <button onClick={() => onDelete(c._id)}>Delete</button>
          <button onClick={() => onSelect(c)}>Select</button>
        </li>
      ))}
    </ul>
  );
}
