import { useState, useEffect, useCallback, useRef } from "react";
import axios from "axios";

export default function useUsers() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  // useRef para evitar fetchUsers duplo no StrictMode
  const hasFetched = useRef(false);

  const fetchUsers = useCallback(async () => {
    if (hasFetched.current) return; // evita re-fetch múltiplo no dev
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:4000/api/users");
      setUsers(res.data);
      hasFetched.current = true;
    } catch (err) {
      console.error(err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const addUser = async (userData) => {
    setSaving(true);
    try {
      const res = await axios.post("http://localhost:4000/api/users", userData);
      // Atualiza imediatamente o estado sem fetch extra
      setUsers(prevUsers => [...prevUsers, res.data]);
      return res.data;
    } catch (err) {
      console.error(err);
    } finally {
      setSaving(false);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      setUsers(prevUsers => prevUsers.filter(u => u._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return { users, loading, saving, addUser, deleteUser };
}
