import React, { createContext, useContext, useState, useEffect } from "react";
import axios from "axios";

const UsersContext = createContext();

const removeDuplicatesById = (usersArray) => {
  const seen = new Set();
  return usersArray.filter((user) => {
    if (seen.has(user._id)) {
      return false;
    }
    seen.add(user._id);
    return true;
  });
};

export const UsersProvider = ({ children }) => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  // Função para obter todos os users do backend
  const fetchUsers = async () => {
    setLoading(true);
    try {
      console.log("[UsersContext] Fetching users...");
      const res = await axios.get("http://localhost:4000/api/users");
      const uniqueUsers = removeDuplicatesById(res.data);
      setUsers(uniqueUsers);
      console.log(`[UsersContext] ${uniqueUsers.length} users carregados.`);
    } catch (err) {
      console.error("[UsersContext] Failed to fetch users:", err);
    } finally {
      setLoading(false);
    }
  };

  // Chama fetchUsers uma vez quando o componente monta
  useEffect(() => {
    fetchUsers();
  }, []);

  // Função para adicionar user
  const addUser = async (userData) => {
    setSaving(true);
    try {
      console.log("[UsersContext] Adding user:", userData);
      const res = await axios.post("http://localhost:4000/api/users", userData);
      console.log("[UsersContext] User adicionado:", res.data);
      await fetchUsers(); // Atualiza a lista após adicionar
      return res.data;
    } catch (err) {
      console.error("[UsersContext] Failed to add user:", err);
      return null;
    } finally {
      setSaving(false);
    }
  };

  // Função para apagar user por id
  const deleteUser = async (id) => {
    try {
      console.log("[UsersContext] Deleting user id:", id);
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      console.log("[UsersContext] User apagado:", id);
      await fetchUsers(); // Atualiza lista após apagar
    } catch (err) {
      console.error("[UsersContext] Failed to delete user:", err);
    }
  };

  return (
    <UsersContext.Provider value={{ users, loading, saving, addUser, deleteUser }}>
      {children}
    </UsersContext.Provider>
  );
};

// Hook para consumir o contexto facilmente
export const useUsers = () => useContext(UsersContext);
