import React, { useState, useEffect } from "react";
import useUsers from "../hooks/useUsers";
import UserForm from "../components/UserForm";
import UserTable from "../components/UserTable";
import Spinner from "../components/Spinner";
import { useTranslation } from "react-i18next";

export default function Home() {
  const { t } = useTranslation();
  const { users, loading, saving, addUser } = useUsers();
  const [lastAdded, setLastAdded] = useState(null);

  const handleAddUser = async (userData) => {
    await addUser(userData);
    // Após atualização da lista, o utilizador mais recente será o último do array
    setLastAdded(users[users.length - 1] || null);
  };

  // Para garantir atualização do lastAdded quando users mudar (ex: fetchUsers)
  useEffect(() => {
    if (users.length > 0) {
      setLastAdded(users[users.length - 1]);
    }
  }, [users]);

  return (
    <div style={{ padding: "1rem" }}>
      <h2>{t("home")}</h2>
      {lastAdded && (
        <h3>
          {t("greeting_full", {
            name: lastAdded.name,
            surname: lastAdded.surname,
            country: lastAdded.country,
            date: new Date(lastAdded.birthday).toLocaleDateString(),
          })}
        </h3>
      )}
      <div
        style={{
          display: "flex",
          gap: "2rem",
          marginTop: "1rem",
          flexWrap: "wrap",
        }}
      >
        <div style={{ flex: 1, minWidth: "300px" }}>
          {saving ? <Spinner /> : <UserForm onUserAdded={handleAddUser} />}
        </div>
        <div style={{ flex: 2, minWidth: "300px" }}>
          {loading ? <Spinner /> : <UserTable users={users} />}
        </div>
      </div>
    </div>
  );
}
