import React from "react";
import { useTranslation } from "react-i18next";

export default function UserTable({ users, onDelete }) {
  const { t, i18n } = useTranslation();

  return (
    <table>
      <thead>
        <tr>
          <th>{t("name")}</th>
          <th>{t("country")}</th>
          <th>{t("birthday")}</th>
          <th>{t("action")}</th>
        </tr>
      </thead>
      <tbody>
        {users.length === 0 ? (
          <tr>
            <td colSpan="4">{t("no_users")}</td>
          </tr>
        ) : (
          users.map((u) => (
            <tr key={u._id}>
              <td>{u.name} {u.surname}</td>
              <td>{u.country}</td>
              <td>
                {new Date(u.birthday).toLocaleDateString(
                  i18n.language === "pt" ? "pt-PT" : "en-US"
                )}
              </td>
              <td>
                <button onClick={() => onDelete(u._id)}>
                  {t("delete")}
                </button>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  );
}
