import React, { useState, useEffect } from "react";
import axios from "axios";
import { useTranslation } from "react-i18next";
import styles from "./UserForm.module.css";

export default function UserForm({ onUserAdded }) {
  const { t } = useTranslation();
  const [user, setUser] = useState({
    name: "",
    surname: "",
    country: "",
    birthday: "",
  });
  const [error, setError] = useState("");
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    const loadCountries = async () => {
      try {
        console.log("üöÄ Carregando pa√≠ses...");
        const res = await axios.get("http://localhost:4000/api/countries");
        console.log("‚úÖ Pa√≠ses carregados:", res.data);
        setCountries(res.data);
      } catch (err) {
        console.error("‚ùå Erro ao carregar pa√≠ses:", err);
      }
    };
    loadCountries();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Valida√ß√£o b√°sica
    if (!user.name) {
      setError(t("required_name"));
      console.warn("‚ö†Ô∏è Nome n√£o preenchido.");
      return;
    }
    if (!user.surname) {
      setError(t("required_surname"));
      console.warn("‚ö†Ô∏è Sobrenome n√£o preenchido.");
      return;
    }
    if (!user.country) {
      setError(t("required_country"));
      console.warn("‚ö†Ô∏è Pa√≠s n√£o selecionado.");
      return;
    }
    if (!user.birthday) {
      setError(t("required_birthday"));
      console.warn("‚ö†Ô∏è Data de anivers√°rio n√£o preenchida.");
      return;
    }

    try {
      console.log("üöÄ A enviar utilizador:", user);
      await axios.post("http://localhost:4000/api/users", user);
      console.log("‚úÖ Utilizador enviado com sucesso.");
      onUserAdded(); // Notifica para atualizar lista (sem passar dados que causam duplica√ß√£o)
      setUser({
        name: "",
        surname: "",
        country: "",
        birthday: "",
      });
    } catch (err) {
      console.error("‚ùå Erro ao enviar utilizador:", err);
      setError(t("error"));
    }
  };

  return (
    <form onSubmit={handleSubmit} className={styles.formContainer}>
      <h3>{t("add_user")}</h3>
      {error && <p className={styles.error}>{error}</p>}
      <input
        name="name"
        value={user.name}
        onChange={handleChange}
        placeholder={t("name")}
        className={styles.nameInput}
      />
      <input
        name="surname"
        value={user.surname}
        onChange={handleChange}
        placeholder={t("surname")}
        className={styles.surnameInput}
      />
      <select
        name="country"
        value={user.country}
        onChange={handleChange}
        className={styles.countrySelect}
      >
        <option value="">{t("select_country")}</option>
        {countries.map((c) => (
          <option key={c._id} value={c.name}>{c.name}</option>
        ))}
      </select>
      <input
        type="date"
        name="birthday"
        value={user.birthday}
        onChange={handleChange}
        className={styles.birthdayInput}
      />
      <button type="submit" className={styles.saveButton}>
        {t("save")}
      </button>
    </form>
  );
}
