import { useState, useEffect } from "react";
import axios from "axios";

export default function useUsers() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);  // <-- spinner só para o save

  useEffect(() => {
    const fetchUsers = async () => {
      setLoading(true);
      try {
        const res = await axios.get("http://localhost:4000/api/users");
        setUsers(res.data);
      } catch (err) {
        console.error(err);
      } finally {
        setLoading(false);
      }
    };
    fetchUsers();
  }, []);

  const addUser = async (userData) => {
    setSaving(true);    // spinner de save
    try {
      const res = await axios.post("http://localhost:4000/api/users", userData);
      setUsers((prev) => [...prev, res.data]);
      return res.data;
    } catch (err) {
      console.error(err);
    } finally {
      setSaving(false);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      setUsers((prev) => prev.filter((u) => u._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  return { users, loading, saving, addUser, deleteUser };
}
