import React, { useState } from "react";
import axios from "axios";

export default function UserForm({ onUserAdded }) {
  const [user, setUser] = useState({
    name: "",
    surname: "",
    country: "",
    birthday: "",
  });

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await axios.post("http://localhost:4000/api/users", user);
    onUserAdded(res.data);
    setUser({
      name: "",
      surname: "",
      country: "",
      birthday: "",
    });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Add User</h3>
      <input name="name" value={user.name} onChange={handleChange} placeholder="Name" />
      <input name="surname" value={user.surname} onChange={handleChange} placeholder="Surname" />
      <input name="country" value={user.country} onChange={handleChange} placeholder="Country" />
      <input type="date" name="birthday" value={user.birthday} onChange={handleChange} />
      <button type="submit">Save</button>
    </form>
  );
}
