const handleSubmit = async (e) => {
    e.preventDefault();
    if (!user.name) {
      setError(t("required_name"));
      return;
    }
    if (!user.surname) {
      setError(t("required_surname"));
      return;
    }
    if (!user.country) {
      setError(t("required_country"));
      return;
    }
    if (!user.birthday) {
      setError(t("required_birthday"));
      return;
    }

    try {
      await axios.post("http://localhost:4000/api/users", user);
      onUserAdded(); // apenas trigger para refetch
      setUser({
        name: "",
        surname: "",
        country: "",
        birthday: "",
      });
    } catch (err) {
      console.error(err);
      setError(t("error"));
    }
  };
