import React from "react";
import styles from "./UserTable.module.css";

export default function UserTable({ users, onDelete }) {
  console.log("UserTable onDelete recebido:", onDelete);

  const handleDelete = (id) => {
    console.log("Pedido para apagar id:", id);
    if (typeof onDelete === "function") {
      onDelete(id);
    } else {
      console.warn("onDelete não é uma função!");
    }
  };

  return (
    <table className={styles.table}>
      <thead>
        <tr>
          <th>Nome</th>
          <th>País</th>
          <th>Aniversário</th>
          <th>Ações</th>
        </tr>
      </thead>
      <tbody>
        {users.length === 0 ? (
          <tr>
            <td colSpan="4">Sem utilizadores</td>
          </tr>
        ) : (
          users.map((user) => (
            <tr key={user._id}>
              <td>{user.name} {user.surname}</td>
              <td>{user.country}</td>
              <td>{new Date(user.birthday).toLocaleDateString()}</td>
              <td>
                <button onClick={() => handleDelete(user._id)}>Apagar</button>
              </td>
            </tr>
          ))
        )}
      </tbody>
    </table>
  );
}
