import { useState, useEffect } from "react";
import axios from "axios";

export default function useUsers() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  const fetchUsers = async () => {
    console.log("üöÄ fetchUsers: iniciando carregamento dos utilizadores...");
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:4000/api/users");
      console.log("‚úÖ fetchUsers: dados recebidos:", res.data);
      setUsers(res.data);
    } catch (err) {
      console.error("‚ùå fetchUsers: erro ao carregar utilizadores:", err);
    } finally {
      setLoading(false);
      console.log("‚èπ fetchUsers: terminou carregamento.");
    }
  };

  useEffect(() => {
    console.log("‚ö° useEffect: componente montado, a carregar utilizadores.");
    fetchUsers();
  }, []);

  const addUser = async (userData) => {
    console.log("üöÄ addUser: a tentar adicionar utilizador:", userData);
    setSaving(true);
    try {
      const res = await axios.post("http://localhost:4000/api/users", userData);
      console.log("‚úÖ addUser: utilizador adicionado com sucesso:", res.data);
      await fetchUsers();  // Atualiza a lista para sincronizar estado
      return res.data;     // devolve o utilizador criado para uso posterior
    } catch (err) {
      console.error("‚ùå addUser: erro ao adicionar utilizador:", err);
      throw err;
    } finally {
      setSaving(false);
      console.log("‚èπ addUser: opera√ß√£o de adicionar utilizador terminada.");
    }
  };

  const deleteUser = async (id) => {
    console.log("üöÄ deleteUser: a tentar apagar utilizador com id:", id);
    try {
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      console.log("‚úÖ deleteUser: utilizador apagado com sucesso:", id);
      await fetchUsers();
    } catch (err) {
      console.error("‚ùå deleteUser: erro ao apagar utilizador:", err);
    }
  };

  return { users, loading, saving, addUser, deleteUser };
}
