import React, { useState, useEffect } from "react";
import axios from "axios";

export default function UserForm({ onUserAdded }) {
  const [user, setUser] = useState({
    name: "",
    surname: "",
    country: "",
    birthday: "",
  });
  const [countries, setCountries] = useState([]);
  const [error, setError] = useState(null);

  // carregar lista de países a partir da base de dados
  useEffect(() => {
    axios
      .get("http://localhost:4000/api/countries")
      .then((res) => setCountries(res.data))
      .catch((err) => console.error(err));
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // validação
    if (!user.name || !user.surname || !user.birthday || !user.country) {
      setError("⚠️ Preencha todos os campos obrigatórios (nome, sobrenome, país, data de nascimento)");
      return;
    }
    setError(null);
    try {
      const res = await axios.post("http://localhost:4000/api/users", user);
      onUserAdded(res.data);
      setUser({
        name: "",
        surname: "",
        country: "",
        birthday: "",
      });
    } catch (err) {
      console.error(err);
      setError("Erro ao salvar utilizador.");
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Add User</h3>
      <input
        name="name"
        value={user.name}
        onChange={handleChange}
        placeholder="Name"
      />
      <input
        name="surname"
        value={user.surname}
        onChange={handleChange}
        placeholder="Surname"
      />
      <select
        name="country"
        value={user.country}
        onChange={handleChange}
      >
        <option value="">Select country</option>
        {countries.map((c) => (
          <option key={c._id} value={c.name}>{c.name}</option>
        ))}
      </select>
      <input
        type="date"
        name="birthday"
        value={user.birthday}
        onChange={handleChange}
      />
      <button type="submit">Save</button>
      {error && <p style={{ color: "red", marginTop: "0.5rem" }}>{error}</p>}
    </form>
  );
}
