import React from "react";
import { useSelector } from "react-redux";

export default function Greeting() {
  let selectedCountry;

  try {
    selectedCountry = useSelector((state) => state?.country?.selectedCountry);
  } catch (e) {
    // fallback se o Redux não estiver configurado
    selectedCountry = null;
  }

  if (!selectedCountry) {
    return <h3>Welcome! Please select a country.</h3>;
  }

  return (
    <h3>
      Hello visitor from {selectedCountry.name} ({selectedCountry.code})
    </h3>
  );
}
