import { useState, useEffect, useCallback } from "react";
import axios from "axios";

export default function useUsers() {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(false);
  const [saving, setSaving] = useState(false);

  const fetchUsers = useCallback(async () => {
    setLoading(true);
    try {
      const res = await axios.get("http://localhost:4000/api/users");
      setUsers(res.data);
    } catch (err) {
      console.error("[useUsers] Failed to fetch users:", err);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchUsers();
  }, [fetchUsers]);

  const addUser = async (userData) => {
    setSaving(true);
    try {
      // Garante que _id não é enviado no userData (precaução)
      const dataToSend = {
        name: userData.name,
        surname: userData.surname,
        country: userData.country,
        birthday: userData.birthday,
      };
      const res = await axios.post("http://localhost:4000/api/users", dataToSend);
      await fetchUsers(); // Atualiza lista no estado após criação
      return res.data;     // Retorna user criado
    } catch (err) {
      console.error("[useUsers] Failed to add user:", err);
      return null;
    } finally {
      setSaving(false);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:4000/api/users/${id}`);
      await fetchUsers();
    } catch (err) {
      console.error("[useUsers] Failed to delete user:", err);
    }
  };

  return { users, loading, saving, addUser, deleteUser };
}
