import { useSelector } from "react-redux";

export default function Greeting() {
  const user = useSelector((state) => state.user?.lastUserAdded);

  if (!user) {
    return null; // não mostra nada até haver utilizador
  }

  const birthDate = new Date(user.birthday).toLocaleDateString();

  return (
    <h3>
      Hello {user.name} {user.surname} from {user.country}, born on {birthDate}!
    </h3>
  );
}
