import React, { useState, useEffect } from "react";
import axios from "axios";
import { useTranslation } from "react-i18next";

export default function UserForm({ onUserAdded }) {
  const { t, i18n } = useTranslation();
  const [user, setUser] = useState({
    name: "",
    surname: "",
    country: "",
    birthday: "",
  });
  const [error, setError] = useState("");
  const [countries, setCountries] = useState([]);

  useEffect(() => {
    const loadCountries = async () => {
      try {
        const res = await axios.get("http://localhost:4000/api/countries");
        setCountries(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    loadCountries();
  }, []);

  const handleChange = (e) => {
    setUser({ ...user, [e.target.name]: e.target.value });
    setError(""); // clear errors on edit
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // validation
    if (!user.name) {
      setError(t("required_name"));
      return;
    }
    if (!user.surname) {
      setError(t("required_surname"));
      return;
    }
    if (!user.country) {
      setError(t("required_country"));
      return;
    }
    if (!user.birthday) {
      setError(t("required_birthday"));
      return;
    }

    try {
      const res = await axios.post("http://localhost:4000/api/users", user);
      onUserAdded(res.data);
      setUser({
        name: "",
        surname: "",
        country: "",
        birthday: "",
      });
    } catch (err) {
      console.error(err);
      setError(t("error"));
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>{t("add_user")}</h3>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <input
        name="name"
        value={user.name}
        onChange={handleChange}
        placeholder={t("name")}
      />
      <input
        name="surname"
        value={user.surname}
        onChange={handleChange}
        placeholder={t("surname")}
      />
      <select
        name="country"
        value={user.country}
        onChange={handleChange}
      >
        <option value="">{t("select_country")}</option>
        {countries.map((c) => (
          <option key={c._id} value={c.name}>{c.name}</option>
        ))}
      </select>
      <label>
        {t("birthday")}
        <span style={{ fontSize: "0.8rem", marginLeft: "5px" }}>
          {i18n.language === "pt" ? "(aaaa-mm-dd)" : "(yyyy-mm-dd)"}
        </span>
      </label>
      <input
        type="date"
        name="birthday"
        value={user.birthday}
        onChange={handleChange}
      />
      <button type="submit">{t("save")}</button>
    </form>
  );
}
